package com.syntax.class03;

public class additionVSConcatentaion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int a=100;
	int b=200;
	
	String x="Hello";
	String y="Friends";
	System.out.println(a+b+x+y);//300HelloFriends
	System.out.println(a+x+b+y);//100Hello200Driends
	System.out.println(x+y+a+b);//HelloFriends100200
	System.out.print(x+y+(a+b));//Hellofriends300
	
	
	//////
	int a=12;
	int b=7;
	
	//
		
		
	}

}
